angular.module('ionicApp',['ngStorage','webstorage']).controller('MainCtrl',function($scope,$localStorage){$scope.strings=['Main Theme','Sunny Theme','Night Theme','Magic Theme'];$scope.cargarTemplates=function(){if($scope.template==0){alert("hola")}}})
function componentPrepare(){$(".select.switch label").each(function(index){var label=$(this);var id=label.attr("for");var select=label.find("select");var $body=$('body');var $bg=$('#bg');var MAX_NUM=4;var selected=0;var values=[];select.find("option").each(function(index){if(index<MAX_NUM){values[index]={value:$(this).val(),label:$(this).html()};if($(this).is(":selected")||select.val()==$(this).val())
selected=index;}});label.find(".switch-toggle").css("width",7.5+values.length*15.5);label.find(".switch-toggle").val(values[selected].value);label.find(".display").html(values[selected].label);label.addClass("selected-"+selected);$body.addClass("bg-"+selected);label.click(function(event){selected=(selected+1)%values.length;select.val(values[selected].value).change();event.preventDefault();});select.change(function(event){var selected=0;for(var i=0;i<values.length;i++){label.removeClass("selected-"+i);$body.removeClass("bg-"+i);if($(this).val()==values[i].value)
selected=i;}
label.find(".display").html(values[selected].label);label.addClass("selected-"+selected);$body.addClass("bg-"+selected);event.preventDefault();});});}
$(document).ready(function(){componentPrepare();});